    fun agregarLugares(db: SQLiteDatabase, lugares: Lugares) {
        val contentVales = ContentValues()
        contentVales.put("nro_lugar", lugares.nro_lugar)
        contentVales.put("nombre", lugares.nombre)
        contentVales.put("zona", lugares.zona)
        contentVales.put("latitud", lugares.latitud)
        contentVales.put("longitud", lugares.longitud)
        contentVales.put("tipoDeLugar", lugares.tipoDeLugar)
        contentVales.put("imagen", lugares.imagen)
        contentVales.put("nro_producto", lugares.nro_producto)
        db.insert("Lugares", null, contentVales)
    }

    fun agregarProductos(db: SQLiteDatabase, productos: Productos) {
        val contentVales = ContentValues()
        contentVales.put("nro_producto", productos.nro_producto)
        contentVales.put("nombre", productos.nombre)
        contentVales.put("precio", productos.precio)
        contentVales.put("imagen", productos.imagen)
        db.insert("Productos", null, contentVales)
    }

    fun obtenerLugaresPorZona(producto: String, zona: String): MutableList<Lugares> {
        val cursor = readableDatabase.rawQuery("SELECT * \n" +
                "FROM Lugares\n" +
                "INNER JOIN Productos\n" +
                "ON Lugares.nro_producto = Productos.nro_producto\n" +
                "WHERE Lugares.nro_producto = (SELECT nro_producto FROM Productos WHERE nombre LIKE \"%${producto}%\")\n" +
                "AND Lugares.zona = \"${zona}\"", arrayOf())
        val listaLugares = mutableListOf<Lugares>()
        while(cursor.moveToNext()) {
            val nroLugar = cursor.getInt(0)
            val nombre = cursor.getString(1)
            val zona = cursor.getString(2)
            val latitud = cursor.getDouble(3)
            val longitud = cursor.getDouble(4)
            val tipoDeLugar = cursor.getString(5)
            val imagen = cursor.getString(6)
            val nroProducto = cursor.getInt(7)
            listaLugares.add(Lugares(nroLugar, nombre, zona, latitud, longitud, tipoDeLugar, imagen, nroProducto))
        }
        cursor.close()
        return listaLugares
    }

    fun obtenerLatitud(lugar: String?, zona: String): String {
        val cursor = readableDatabase.rawQuery("Select latitud\n" +
                "from Lugares\n" +
                "where Lugares.nombre like \"%${lugar}%\"\n" +
                "and Lugares.zona like \"%${zona}%\"", arrayOf())
        var latitud = String()
        while(cursor.moveToNext()) {
            latitud = cursor.getString(0)
        }
        cursor.close()
        return latitud
    }

    fun obtenerLongitud(lugar: String?, zona: String): String {
        val cursor = readableDatabase.rawQuery("Select longitud\n" +
                "from Lugares\n" +
                "where Lugares.nombre like \"%${lugar}%\"\n" +
                "and Lugares.zona like \"%${zona}%\"", arrayOf())
        var longitud = String()
        while(cursor.moveToNext()) {
            longitud = cursor.getString(0)
        }
        cursor.close()
        return longitud
    }

    fun obtenerDescripcion(producto: String): String {
        val cursor = readableDatabase.rawQuery("Select longitud\n" +
                "from Productos\n" +
                "where Productos.nombre like \"%${producto}%\"", arrayOf())
        var descrip = String()
        while(cursor.moveToNext()) {
            descrip = cursor.getString(0)
        }
        cursor.close()
        return descrip
